export default Alert;

import React from 'react';
import DateJumpFilter from './index';

describe('DateJumpFilter', () => {
  let props, wrapper, fakeUpdateDateJump;

  beforeEach(() => {
    fakeUpdateDateJump = sinon.fake();

    props = {
      updateDateJump: fakeUpdateDateJump,
      selectedDateJump: 'week'
    }

    wrapper = shallow(<DateJumpFilter {...props} />)

    return {
      props,
      wrapper
    }
  }
  )

  it('Calls prop updateDateJump', () => {
    wrapper.instance().updateDateJump('year');
    sinon.assert.calledOnce(fakeUpdateDateJump)
  })

  it('Returns and DO NOT call prop updateDateJump', () => {
    wrapper.instance().updateDateJump('week');
    sinon.assert.notCalled(fakeUpdateDateJump)
  })

  it('Menue default close and toggles', () => {
    expect(wrapper.state().dropdownOpen).to.equal(false);
    wrapper.instance().toggle();
    expect(wrapper.state().dropdownOpen).to.equal(true);
  })

  it('Returns selectedDateJump', () => {
    expect(wrapper.instance().getSelectedDateJump()).to.equal('Weekly');
    wrapper.setProps({ selectedDateJump: 'day' })
    expect(wrapper.instance().getSelectedDateJump()).to.equal('Daily');
    wrapper.setProps({ selectedDateJump: 'month' })
    expect(wrapper.instance().getSelectedDateJump()).to.equal('Monthly');
    wrapper.setProps({ selectedDateJump: 'year' })
    expect(wrapper.instance().getSelectedDateJump()).to.equal('Yearly');
    wrapper.setProps({ selectedDateJump: '' })
    expect(wrapper.instance().getSelectedDateJump()).to.equal('Weekly');
  })

  it('renders all options', () => {
    expect(wrapper.find('DropdownItem')).to.have.lengthOf(4);
  })


})

import React from 'react';
import PropTypes from 'prop-types';
import { Dropdown, DropdownItem, DropdownMenu, DropdownToggle } from 'reactstrap';

import './styles.css';

class DateJumpFilter extends React.Component {
  state = {
    dropdownOpen: false
  };

  updateDateJump = (dateJump) => {
    if (dateJump === this.props.selectedDateJump) {
      return;
    }

    this.props.updateDateJump(dateJump);
  };

  toggle = () => {
    this.setState(prevState => ({
      dropdownOpen: !prevState.dropdownOpen
    }));
  };

  getSelectedDateJump() {
    switch (this.props.selectedDateJump) {
      case 'day':
        return 'Daily';
      case 'month':
        return 'Monthly';
      case 'year':
        return 'Yearly';
      case 'week':
        return 'Weekly';
      default:
        return 'Weekly';
    }
  }

  render() {
    return (
      <Dropdown className='date-jump-wrap' isOpen={this.state.dropdownOpen} toggle={this.toggle}>
        <DropdownToggle className='date-jump-type shadowed'>
          <i className="fa fa-calendar mr-2"></i>
          {this.getSelectedDateJump()}
        </DropdownToggle>
        <DropdownMenu>
          <DropdownItem onClick={() => this.updateDateJump('year')}>Yearly</DropdownItem>
          <DropdownItem onClick={() => this.updateDateJump('month')}>Monthly</DropdownItem>
          <DropdownItem onClick={() => this.updateDateJump('week')}>Weekly</DropdownItem>
          <DropdownItem onClick={() => this.updateDateJump('day')}>Daily</DropdownItem>
        </DropdownMenu>
      </Dropdown>
    );
  }
}

DateJumpFilter.propTypes = {
  updateDateJump: PropTypes.func.isRequired,
  selectedDateJump: PropTypes.string
};

export default DateJumpFilter;

import React from 'react';
import Filters from './index'

describe('Filters', () => {

  let props, wrapper, fakeUpdateLanguage, fakeUpdateDateJump, fakeUpdateViewType;

  beforeEach(() => {

    fakeUpdateLanguage = sinon.fake();
    fakeUpdateDateJump = sinon.fake();
    fakeUpdateViewType = sinon.fake();

    props = {
      updateLanguage: fakeUpdateLanguage,
      updateDateJump: fakeUpdateDateJump,
      updateViewType: fakeUpdateViewType,
      selectedLanguage: '',
      selectedViewType: '',
      selectedDateJump: ''
    }

    wrapper = shallow(<Filters {...props}></Filters>)

    return {
      props,
      wrapper
    }
  }
  )

  it('Renders 3 filter options', () => {
    expect(wrapper.find('div')).to.have.length(4)
    expect(wrapper.find('LanguageFilter').props()).to.have.property('selectedLanguage')
    expect(wrapper.find('LanguageFilter').props()).to.have.property('updateLanguage')
    expect(wrapper.find('DateJumpFilter').props()).to.have.property('updateDateJump')
    expect(wrapper.find('DateJumpFilter').props()).to.have.property('selectedDateJump')
    expect(wrapper.find('ViewFilter').props()).to.have.property('selectedViewType')
    expect(wrapper.find('ViewFilter').props()).to.have.property('updateViewType')
  })

  it('Passes props to child components', () => {
    expect(wrapper.find('LanguageFilter').props().selectedLanguage).to.equal('');
    expect(wrapper.find('LanguageFilter').props().updateLanguage).to.be.a('function');
    expect(wrapper.find('DateJumpFilter').props().updateDateJump).to.be.a('function');
    expect(wrapper.find('DateJumpFilter').props().selectedDateJump).to.equal('');
    expect(wrapper.find('ViewFilter').props().selectedViewType).to.equal('');
    expect(wrapper.find('ViewFilter').props().updateViewType).to.be.a('function');
  })
})

import React from 'react';
import PropTypes from 'prop-types';

import LanguageFilter from './language-filter';
import ViewFilter from './view-filter';
import './styles.css';
import DateJumpFilter from './date-jump-filter';

const Filters = (props) => (
  <div className="filters-wrap mt-3 mt-sm-3 mt-md-0 mt-xl-0 mt-lg-0">
    <div className="filter-item">
      <LanguageFilter
        selectedLanguage={props.selectedLanguage}
        updateLanguage={props.updateLanguage}
      />
    </div>
    <div className="filter-item">
      <DateJumpFilter
        updateDateJump={props.updateDateJump}
        selectedDateJump={props.selectedDateJump}
      />
    </div>
    <div className="filter-item d-none d-sm-none d-md-none d-xl-block d-lg-block">
      <ViewFilter
        selectedViewType={props.selectedViewType}
        updateViewType={props.updateViewType}
      />
    </div>
  </div>
);

Filters.propTypes = {
  updateLanguage: PropTypes.func.isRequired,
  updateViewType: PropTypes.func.isRequired,
  updateDateJump: PropTypes.func.isRequired,
  selectedLanguage: PropTypes.string,
  selectedViewType: PropTypes.string,
  selectedDateJump: PropTypes.string
};

export default Filters;

import React from 'react';
import ReactDOM from 'react-dom';
import PropTypes from 'prop-types';
import classNames from 'classnames';

import './styles.css';
import languages from './languages';

class LanguageFilter extends React.Component {
  filterInputRef = React.createRef();

  state = {
    filterText: '',
    selectedIndex: 0,
    showDropdown: false
  };

  focusFilterInput = () => {
    this.filterInputRef.current.focus();
  };

  componentDidUpdate(prevProps, prevState) {
    // only scroll into view if the active item changed since last render
    if (prevState.selectedIndex !== this.state.selectedIndex) {
      this.ensureSelectedVisible();
    }

    // If the dropdown has just been made visible focus the input
    if (this.state.showDropdown && !prevState.showDropdown) {
      this.focusFilterInput();
    }
  }

  ensureSelectedVisible() {
    const itemComponent = this.refs.activeItem;
    if (!itemComponent) {
      return;
    }

    const domNode = ReactDOM.findDOMNode(itemComponent);
    if (!domNode) {
      return;
    }

    domNode.scrollIntoView({ block: 'end' });
  }

  getFilteredLanguages() {
    let availableLanguages = [...languages];

    if (this.state.filterText) {
      availableLanguages = availableLanguages.filter(language => {
        const languageText = language.title.toLowerCase();
        const selectedText = this.state.filterText.toLowerCase();

        return languageText.indexOf(selectedText) >= 0;
      });
    }

    return availableLanguages;
  }

  renderLanguages() {
    let availableLanguages = this.getFilteredLanguages();

    return availableLanguages.map((language, counter) => {
      const isSelectedIndex = counter === this.state.selectedIndex;

      // This will be used in making sure of the element visibility
      const refProp = isSelectedIndex ? { ref: 'activeItem' } : {};

      return (
        <a className={ classNames('select-menu-item', { 'active-item': isSelectedIndex }) }
           { ...refProp }
           onMouseDown={ () => this.selectLanguage(counter) }
           key={ counter }>
          <span className="select-menu-item-text">{ language.title }</span>
        </a>
      );
    });
  }

  onKeyDown = e => {
    const { selectedIndex } = this.state;

    const isEnterKey = e.keyCode === 13;
    const isUpKey = e.keyCode === 38;
    const isDownKey = e.keyCode === 40;

    if (!isUpKey && !isDownKey && !isEnterKey) {
      return;
    }

    const filteredLanguages = this.getFilteredLanguages();
    e.preventDefault();

    // arrow up/down button should select next/previous list element
    if (isUpKey && selectedIndex > 0) {
      this.setState(prevState => ({
        selectedIndex: prevState.selectedIndex - 1
      }));
    } else if (isDownKey && selectedIndex < (filteredLanguages.length - 1)) {
      this.setState(prevState => ({
        selectedIndex: prevState.selectedIndex + 1
      }));
    } else if (isEnterKey && filteredLanguages[selectedIndex]) {
      this.selectLanguage(selectedIndex);
    }
  };

  selectLanguage = (selectedIndex) => {
    const filteredLanguages = this.getFilteredLanguages();
    const selectedLanguage = filteredLanguages[selectedIndex];
    if (!selectedLanguage) {
      return;
    }

    this.setState({
      filterText: '',
      showDropdown: false
    });

    this.props.updateLanguage(selectedLanguage.value);
  };

  hideDropdown = () => {
    this.setState({
      showDropdown: false,
      filterText: ''
    });
  };

  filterLanguages = (e) => {
    this.setState({
      filterText: e.target.value,
      selectedIndex: 0      // Reset and select the first language
    });
  };

  getLanguageDropdown() {
    return (
      <div className="language-select">
        <div className="select-menu-header">
          <span className="select-menu-title">Search Language</span>
        </div>
        <div className="select-menu-filters">
          <div className="select-menu-text-filter">
            <input type="text"
                   className="form-control"
                   placeholder="Filter Languages"
                   ref={ this.filterInputRef }
                   onBlur={ this.hideDropdown }
                   onChange={ this.filterLanguages }
                   onKeyDown={ this.onKeyDown }
            />
          </div>
        </div>
        <div className="select-menu-list">
          { this.renderLanguages() }
        </div>
      </div>
    );
  }

  toggleDropdown = () => {
    this.setState(prevState => ({
      showDropdown: !prevState.showDropdown
    }));
  };

  render() {
    return (
      <div className='language-filter-wrap'>
        <button onClick={ this.toggleDropdown } className="btn btn-light language-filter shadowed">
          <i className="fa fa-filter mr-2"></i>
          { this.props.selectedLanguage || 'All Languages' }
        </button>
        { this.state.showDropdown && this.getLanguageDropdown() }
      </div>
    );
  }
}

LanguageFilter.propTypes = {
  updateLanguage: PropTypes.func.isRequired,
  selectedLanguage: PropTypes.string
};

export default LanguageFilter;

import React from 'react';
import classNames from 'classnames';
import PropTypes from 'prop-types';

import './styles.css';

class ViewFilter extends React.Component {
  state = {};

  changeSelected = (viewType) => {
    this.props.updateViewType(viewType);
  };

  render() {
    return (
      <div className="view-type-wrap">
        <div className="view-type btn shadowed cursor-default">
          <button onClick={() => this.changeSelected('grid')} className={classNames({ active: this.props.selectedViewType === 'grid' })}>
            <i className="fa fa-table mr-2"></i>
            Grid
          </button>
          <button onClick={() => this.changeSelected('list')} className={classNames({ active: this.props.selectedViewType === 'list' })}>
            <i className="fa fa-list mr-2"></i>
            List
          </button>
        </div>
      </div>
    );
  }
}

ViewFilter.propTypes = {
  updateViewType: PropTypes.func.isRequired,
  selectedViewType: PropTypes.string,
};

export default ViewFilter;

import React from 'react';
import ViewFilter from './index';

describe('ViewFilter', () => {

  let props, wrapper, fakeUpdateViewType;

  beforeEach(() => {

    fakeUpdateViewType = sinon.fake();

    props = {
      updateViewType: fakeUpdateViewType,
      selectedViewType: 'list'
    }

    wrapper = shallow(<ViewFilter {...props}></ViewFilter>)

    return {
      props,
      wrapper
    }
  }
  )

  it('Changes to grid', () => {
    wrapper.find('button').at(0).simulate('click');
    sinon.assert.calledOnce(fakeUpdateViewType)
    sinon.assert.calledWith(fakeUpdateViewType.firstCall, 'grid')
  })

  it('Changes to list', () => {
    wrapper.find('button').at(1).simulate('click');
    sinon.assert.calledOnce(fakeUpdateViewType)
    sinon.assert.calledWith(fakeUpdateViewType.firstCall, 'list')
  })

  it('renders options', () => {
    expect(wrapper.find('div')).to.have.length(2)
    expect(wrapper.find('i')).to.have.length(2)

    const grid = wrapper.find('button').at(0);
    expect(grid.text()).to.equal('Grid');
    const list = wrapper.find('button').at(1);
    expect(list.text()).to.equal('List');

    expect(wrapper.find('button').at(1).hasClass('active')).to.equal(true)
    wrapper.setProps({ selectedViewType: 'grid' })
    expect(wrapper.find('button').at(0).hasClass('active')).to.equal(true)
  })

})
